        /**
         * LOOP 1: FOR LOOP - Generate Featured Article
         * Creates the main featured article using loop logic
         */
        function generateFeaturedArticle() {
            const featuredContainer = document.getElementById('featured-article');
            // Use first article from array using FOR loop index
            for (let i = 0; i < 1; i++) {
                const article = newsData[i];
                featuredContainer.innerHTML = `
                    <div class="featured-image" style="background-image: url('${article.image}')"></div>
                    <div class="featured-content">
                        <div class="category">Featured Story</div>
                        <h2>${article.title}</h2>
                        <div class="date">${article.date}</div>
                    </div>
                `;
            }
        }

        /**
         * LOOP 2: FOR LOOP - Generate News Grids
         * Creates multiple news grids with different layouts
         */
        function generateNewsGrids() {
            const newsContainer = document.getElementById('news-container');
            let articleIndex = 1; // Start from second article (first is featured)

            // Create two 2-column grids
            for (let gridIndex = 0; gridIndex < 2; gridIndex++) {
                const grid = document.createElement('div');
                grid.className = 'news-grid two-column';
                
                // FOR LOOP to add 2 articles per grid
                for (let cardIndex = 0; cardIndex < 2; cardIndex++) {
                    if (articleIndex < newsData.length) {
                        const article = newsData[articleIndex];
                        const newsCard = createNewsCard(article);
                        grid.appendChild(newsCard);
                        articleIndex++;
                    }
                }
                
                newsContainer.appendChild(grid);
            }

            // Create one 3-column grid
            const threeColumnGrid = document.createElement('div');
            threeColumnGrid.className = 'news-grid three-column';
            
            // FOR LOOP to add 3 articles to the grid
            for (let cardIndex = 0; cardIndex < 3; cardIndex++) {
                if (articleIndex < newsData.length) {
                    const article = newsData[articleIndex];
                    const newsCard = createNewsCard(article);
                    threeColumnGrid.appendChild(newsCard);
                    articleIndex++;
                }
            }
            
            newsContainer.appendChild(threeColumnGrid);

            // Create additional grids using remaining articles
            const remainingArticles = newsData.length - articleIndex;
            if (remainingArticles > 0) {
                const finalGrid = document.createElement('div');
                finalGrid.className = 'news-grid three-column';
                
                // FOR LOOP for remaining articles
                for (let i = articleIndex; i < newsData.length; i++) {
                    const article = newsData[i];
                    const newsCard = createNewsCard(article);
                    finalGrid.appendChild(newsCard);
                }
                
                newsContainer.appendChild(finalGrid);
            }
        }

        /**
         * Helper function to create individual news cards
         */
        function createNewsCard(article) {
            const newsCard = document.createElement('div');
            newsCard.className = 'news-card';
            
            newsCard.innerHTML = `
                <div class="card-image" style="background-image: url('${article.image}')"></div>
                <div class="card-content">
                    <div class="category">${article.category}</div>
                    <h3>${article.title}</h3>
                    <div class="date">${article.date}</div>
                </div>
            `;
            
            return newsCard;
        }

        /**
         * LOOP 3: FOREACH LOOP - Add Event Listeners
         * Uses forEach to add click events to all news cards
         */
        function addEventListeners() {
            // Wait for cards to be created, then add listeners
            setTimeout(() => {
                const newsCards = document.querySelectorAll('.news-card, .items1');
                
                // FOREACH LOOP to add click events
                newsCards.forEach((card, index) => {
                    card.addEventListener('click', function() {
                        const title = this.querySelector('h2, h3').textContent;
                        alert(`Opening article ${index + 1}: "${title}"`);
                    });
                });
            }, 100);
        }

        /**
         * LOOP 4: FOR...OF LOOP - Handle Scroll Animations
         * Uses for...of loop to animate elements on scroll
         */
        function initScrollAnimations() {
            const observer = new IntersectionObserver((entries) => {
                // FOR...OF LOOP to handle each observed entry
                for (const entry of entries) {
                    if (entry.isIntersecting) {
                        entry.target.classList.add('visible');
                        entry.target.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
                    }
                }
            }, {
                threshold: 0.1
            });

            // Observe all elements that need animation
            setTimeout(() => {
                const animatedElements = document.querySelectorAll('.news-card, .items1, .final-section');
                // FOR...OF LOOP to observe each element
                for (const element of animatedElements) {
                    observer.observe(element);
                }
            }, 200);
        }

        /**
         * LOOP 5: WHILE LOOP - Simulate Loading Process
         * Demonstrates while loop with loading simulation
         */
        function simulateLoading() {
            const loadingElement = document.getElementById('loading');
            let loadingCounter = 0;
            const maxLoading = 3;

            const loadingInterval = setInterval(() => {
                // WHILE LOOP concept: continue while counter is less than max
                loadingCounter++;
                
                if (loadingCounter >= maxLoading) {
                    loadingElement.style.display = 'none';
                    clearInterval(loadingInterval);
                }
            }, 1000);
        }

        /**
         * LOOP 6: FOR...IN LOOP - Process Navigation Items
         * Uses for...in loop to enhance navigation
         */
        function enhanceNavigation() {
            const navItems = {
                'About': '#about',
                'Apply': '#apply', 
                'News': '#news'
            };

            // FOR...IN LOOP to process navigation items
            for (const itemText in navItems) {
                const navLink = document.querySelector(`nav a[href="${navItems[itemText]}"]`);
                if (navLink) {
                    // Add smooth scrolling with loop-generated offset
                    navLink.addEventListener('click', function(e) {
                        e.preventDefault();
                        
                        // Simple animation using loop logic
                        let currentScroll = window.pageYOffset;
                        const targetScroll = 0;
                        const step = (currentScroll - targetScroll) / 10;
                        
                        const scrollAnimation = () => {
                            if (Math.abs(currentScroll - targetScroll) > 1) {
                                currentScroll -= step;
                                window.scrollTo(0, currentScroll);
                                requestAnimationFrame(scrollAnimation);
                            }
                        };
                        
                        scrollAnimation();
                    });
                }
            }
        }

        /**
         * LOOP 7: FOR LOOP - Dynamic Color Generation
         * Generates dynamic colors for categories using loops
         */
        function applyDynamicColors() {
            const colors = ['#32CD32', '#FF6B6B', '#4ECDC4', '#45B7D1', '#FFA726'];
            
            setTimeout(() => {
                const categoryElements = document.querySelectorAll('.category');
                
                // FOR LOOP to apply colors based on index
                for (let i = 0; i < categoryElements.length; i++) {
                    const colorIndex = i % colors.length;
                    categoryElements[i].style.color = colors[colorIndex];
                }
            }, 300);
        }

        /**
         * LOOP 8: FOREACH LOOP - Navbar Scroll Effect
         * Uses forEach to handle scroll effects on navigation
         */
        function initNavbarEffects() {
            window.addEventListener('scroll', function() {
                const nav = document.querySelector('nav');
                const scrollY = window.scrollY;
                
                if (scrollY > 50) {
                    nav.style.backgroundColor = 'rgba(17, 17, 17, 0.95)';
                } else {
                    nav.style.backgroundColor = 'rgba(17, 17, 17, 0.9)';
                }

                // Apply parallax effect to hero title using loop logic
                const heroTitle = document.querySelector('#page1 h1');
                if (heroTitle) {
                    heroTitle.style.transform = `translateY(${scrollY * 0.5}px)`;
                }
            });
        }

        /**
         * MAIN INITIALIZATION FUNCTION
         * Calls all loop-based functions to build the page
         */
        function initializePage() {
            // Execute all loop-based functions
            generateFeaturedArticle(); // FOR loop for featured article  
            generateNewsGrids();       // Multiple FOR loops for news grids
            addEventListeners();       // FOREACH loop for events
            initScrollAnimations();    // FOR...OF loop for animations
            simulateLoading();         // WHILE loop concept for loading
            enhanceNavigation();       // FOR...IN loop for navigation
            applyDynamicColors();      // FOR loop for styling
            initNavbarEffects();       // FOREACH loop for scroll effects

        }

        /**
         * START THE APPLICATION
         * Initialize everything when DOM is ready
         */
        document.addEventListener('DOMContentLoaded', function() {
            console.log('ðŸš€ DOM loaded, initializing loop-based content generation...');
            initializePage();
        });

        /**
         * BONUS: Interactive Loop Demonstration
         * This function demonstrates loops in the console for educational purposes
         */
        function demonstrateLoops() {
            console.log('\n=== JAVASCRIPT LOOPS DEMONSTRATION ===\n');
            
            // 1. FOR LOOP demonstration
            console.log('1. FOR LOOP - Counting articles:');
            for (let i = 1; i <= 5; i++) {
                console.log(`   Article ${i} loaded`);
            }
            
            // 2. WHILE LOOP demonstration
            console.log('\n2. WHILE LOOP - Processing queue:');
            let queueItems = 3;
            while (queueItems > 0) {
                console.log(`   Processing item ${queueItems}`);
                queueItems--;
            }
            
            // 3. FOREACH LOOP demonstration
            console.log('\n3. FOREACH LOOP - Processing categories:');
            categories.forEach((category, index) => {
                console.log(`   ${index + 1}. ${category}`);
            });
            
            // 4. FOR...OF LOOP demonstration
            console.log('\n4. FOR...OF LOOP - Checking images:');
            for (const image of images) {
                console.log(`   âœ“ Image URL validated: ${image.substring(0, 50)}...`);
            }
            
            console.log('\n=== LOOPS DEMONSTRATION COMPLETE ===\n');
        }

        // Run loop demonstration
        setTimeout(demonstrateLoops, 1000);


